import React from "react";
import {
  DragDropContext,
  Droppable,
  OnDragEndResponder,
} from "react-beautiful-dnd";
import ColorSwatch from "./ColorSwatch";
import { ColorSwatch as ColorSwatchType } from "../types";

interface ColorRampListProps {
  swatches: ColorSwatchType[];
  activeSwatchId: number | null;
  onLValueChange: (id: number, value: number) => void;
  onSwatchClick: (id: number) => void;
  onReorder: OnDragEndResponder;
}

const ColorRampList: React.FC<ColorRampListProps> = ({
  swatches,
  activeSwatchId,
  onLValueChange,
  onSwatchClick,
  onReorder,
}) => {
  return (
    <DragDropContext onDragEnd={onReorder}>
      <Droppable droppableId="color-ramp">
        {(provided) => (
          <div
            className="color-ramps"
            ref={provided.innerRef}
            {...provided.droppableProps}
          >
            {swatches.map((swatch, index) => (
              <ColorSwatch
                key={swatch.id}
                swatch={swatch}
                isActive={swatch.id === activeSwatchId}
                onLValueChange={onLValueChange}
                onClick={onSwatchClick}
                index={index}
              />
            ))}
            {provided.placeholder}
          </div>
        )}
      </Droppable>
    </DragDropContext>
  );
};

export default ColorRampList;
